import React, { Component } from "react";
import "./App.css";
import Person from "./Person/Person";

class App extends Component {
  state = {
    persons: [
      { id: "1", name: "Max", age: 28 },
      { id: "2", name: "Manu", age: 29 },
      { id: "3", name: "Stephanie", age: 26 },
    ],
    otherState: "some other value",
    display: false,
  };
  nameChangeHandler = (e, index) => {
    const personIndex = this.state.persons.findIndex()
    this.setState({
      persons: [
        { name: "Maximillian", age: 28 },
        { name: e.target.value, age: 29 },
        { name: "Stephanie", age: 27 },
      ],
    });
  };
  style = {
    border: "1px solid blue",
    backgroundColor: "white",
    font: "inherit",
    padding: "8px",
    cursor: "pointer",
  };
  displayHandler = () => {
    const displaychange = this.state.display;
    this.setState({ display: !displaychange });
  };
  deleteHandler = (index) => {
    console.log(index);
    const persons = [...this.state.persons];
    persons.splice(index, 1);
    this.setState({ persons });
  };
  render() {
    let persons = null;
    if (this.state.display) {
      persons = (
        <div>
          {this.state.persons.map((person, index) => (
            <Person
              change={(e) => this.nameChangeHandler(e, person.id)}
              key={person.id}
              click={() => this.deleteHandler(index)}
              name={person.name}
              age={person.age}
            />
          ))}
        </div>
      );
    }
    return (
      <div className="App">
        <h1>Hi, I'm a React App</h1>
        <p>This is really working!</p>
        <button style={this.style} onClick={this.displayHandler}>
          Switch Name
        </button>
        {persons}
      </div>
    );
  }
  // return React.createElement('div', {className: 'App'}, React.createElement('h1', null, 'Does this work now?'));
}

export default App;
