import React, { Component } from "react";
import "./App.css";
import Person from "./Person/Person";

class App extends Component {
  state = {
    persons: [
      { name: "Max", age: 28 },
      { name: "Manu", age: 29 },
      { name: "Stephanie", age: 26 },
    ],
    otherState: "some other value",
    display : false
  };

  switchNameHandler = (newName) => {
    // console.log('Was clicked!');
    // DON'T DO THIS: this.state.persons[0].name = 'Maximilian';
    this.setState({
      persons: [
        { name: newName, age: 28 },
        { name: "Manu", age: 29 },
        { name: "Stephanie", age: 27 },
      ],
    });
  };
  nameChangeHandler = (e) => {
    this.setState({
      persons: [
        { name: 'Maximillian', age: 28 },
        { name: e.target.value, age: 29 },
        { name: "Stephanie", age: 27 },
      ],
    })
  }
  style = {
    border : '1px solid blue',
    backgroundColor : 'white',
    font : 'inherit',
    padding : '8px',
    cursor : 'pointer'
  }
  displayHandler = () => {
    const displaychange = this.state.display
    this.setState({ display : !displaychange })
  }
  render(){
    let persons = null
    return (
      <div className="App">
        <h1>Hi, I'm a React App</h1>
        <p>This is really working!</p>
        <button style={this.style} onClick={this.displayHandler}>Switch Name</button>
        <div>
          <Person
            name={this.state.persons[0].name}
            age={this.state.persons[0].age}
          />
          <Person
            name={this.state.persons[1].name}
            age={this.state.persons[1].age}
            click={this.switchNameHandler.bind(this, 'Maximaza')}
            change={this.nameChangeHandler}
          >
            My Hobbies: Racing
          </Person>
          <Person
            name={this.state.persons[2].name}
            age={this.state.persons[2].age}
          />
        </div>
      </div>
    );
  }
  // return React.createElement('div', {className: 'App'}, React.createElement('h1', null, 'Does this work now?'));
};

export default App;
