import React, { useState } from "react";
import "./App.css";
import Person from "./Person/Person";

const App = (props) => {
  const [personState, setPersonState] = useState({
    persons: [
      { name: "Max", age: 28 },
      { name: "Manu", age: 29 },
      { name: "Stephanie", age: 26 },
    ],
    otherState: "some other value",
    display : false
  });

  const switchNameHandler = (newName) => {
    // console.log('Was clicked!');
    // DON'T DO THIS: this.state.persons[0].name = 'Maximilian';
    setPersonState({
      persons: [
        { name: newName, age: 28 },
        { name: "Manu", age: 29 },
        { name: "Stephanie", age: 27 },
      ],
    });
  };
  const nameChangeHandler = (e) => {
    setPersonState({
      persons: [
        { name: 'Maximillian', age: 28 },
        { name: e.target.value, age: 29 },
        { name: "Stephanie", age: 27 },
      ],
    })
  }
  const style = {
    border : '1px solid blue',
    backgroundColor : 'white',
    font : 'inherit',
    padding : '8px',
    cursor : 'pointer'
  }
  const displayHandler = () => {
    const displaychange = personState.display
    setPersonState({ display : !displaychange })
  }
  return (
    <div className="App">
      <h1>Hi, I'm a React App</h1>
      <p>This is really working!</p>
      <button style={style} onClick={displayHandler}>Switch Name</button>
      {personState.display ? <div>
        'Hi'
        {/* <Person
          name={personState.persons[0].name}
          age={personState.persons[0].age}
        />
        <Person
          name={personState.persons[1].name}
          age={personState.persons[1].age}
          click={switchNameHandler.bind(this, 'Maximaza')}
          change={nameChangeHandler}
        >
          My Hobbies: Racing
        </Person>
        <Person
          name={personState.persons[2].name}
          age={personState.persons[2].age}
        /> */}
      </div> : null}
    </div>
  );
  // return React.createElement('div', {className: 'App'}, React.createElement('h1', null, 'Does this work now?'));
};

export default App;
